        aa = unit_to_bytes(abytesunit[0], abytesunit[1]);
        if (isNaN(aa)) {
            aa = 0;
        }
        bbytesunit = b[0].split(/\s+/);
        bb = unit_to_bytes(bbytesunit[0], bbytesunit[1]);
        if (isNaN(bb)) {
            bb = 0;
        }
        return aa - bb;
